select.if:
SELECT *
    FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM BOOK ORDER BY bTITLE) A)
    WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT *
    FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM BOOK WHERE bTITLE LIKE '%' || #{schWord} || '%' OR bWRITER LIKE '%'||#{schWord}||'%' ORDER BY bTITLE) A)
    WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT *
    FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM BOOK WHERE bTITLE LIKE '%'||#{schWord}||'%' ORDER BY bTITLE) A)
    WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT *
    FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM BOOK WHERE bWRITER LIKE '%'||#{schWord}||'%' ORDER BY bTITLE) A)
    WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE BOOK SET bTITLE = #{btitle},
                bWRITER = #{bwriter}
    WHERE bNUM = #{bnum}
